from langchain_community.vectorstores import Chroma, FAISS
from langchain_community.embeddings import HuggingFaceEmbeddings
from typing import List, Dict

class MultiIndexRAG:
    def __init__(self):
        self.embeddings = HuggingFaceEmbeddings(model_name='all-MiniLM-L6-v2')
        self.indices = {}

    def create_index(self, name: str, documents: List[Document], index_type: str = 'chroma'):
        if index_type == 'chroma':
            vectorstore = Chroma.from_documents(documents=documents, embedding=self.embeddings, collection_name=name)
        else:
            vectorstore = FAISS.from_documents(documents=documents, embedding=self.embeddings)
            vectorstore.save_local(f'./faiss_{name}')

        self.indices[name] = {
            'vectorstore': vectorstore,
            'retriever': vectorstore.as_retriever(search_kwargs={'k': 5})
        }

